self.__next_f.push([1,"/* For our datagrail consent banner */
/* https://docs.datagrail.io/docs/consent/banner/css-customization/ */

:host(.dg-consent-banner) {
 /* Fonts - matching Groq Console system */
 --dg-primary-font: Inter, -apple-system, BlinkMacSystemFont, "Segoe UI",
 Roboto, "Helvetica Neue", Arial, sans-serif;
 --dg-secondary-font: Montserrat, -apple-system, BlinkMacSystemFont, "Segoe UI",
 Roboto, "Helvetica Neue", Arial, sans-serif;

 /* General banner styling */
 --dg-consent-background-color: rgb(255, 255, 255);
 --dg-consent-background-border: rgb(231, 229, 228);
 --consent-border-radius: 12px;

 /* Body text styling */
 --dg-body-font-size: 14px;
 --dg-body-font-weight: 400;
 --dg-body-font-color: rgb(118, 111, 107);
 --dg-body-line-height: 1.5;

 /* Heading styling */
 --dg-heading-font-size: 18px;
 --dg-heading-font-weight: 600;
 --dg-heading-font-color: rgb(12, 10, 9);
 --dg-heading-line-height: 1.4;

 /* Title styling */
 --dg-title-font-size: 16px;
 --dg-title-font-weight: 500;
 --dg-title-font-color: rgb(30, 30, 30);
 --dg-title-line-height: 1.4;

 /* Button styling */
 --dg-button-border: rgb(231, 229, 228) 1px solid;
 --dg-button-primary-background: rgb(30, 30, 30);
 --dg-button-primary-color: rgb(248, 248, 247);
 --dg-button-secondary-background: rgb(243, 243, 242);
 --dg-button-secondary-color: rgb(28, 25, 23);

 /* Category styling */
 --dg-policy-option-heading-size: 15px;
 --dg-policy-option-heading-weight: 500;
 --dg-policy-option-heading-color: rgb(30, 30, 30);
 --dg-policy-option-heading-enabled-color: rgb(245, 80, 54);
 --dg-policy-option-chevron-size: 16;

 /* Category description */
 --dg-policy-option-description-font-size: 13px;
 --dg-policy-option-description-font-weight: 400;
 --dg-policy-option-description-font-color: rgb(118, 111, 107);

 /* Essential categories */
 --dg-policy-option-essential-label-font-size: 12px;
 --dg-policy-option-essential-label-font-weight: 500;
 --dg-policy-option-essential-label-font-color: rgb(118, 111, 107);

 /* Slider styling - inspired by switch component */
 --dg-slider-primary: rgb(231, 229, 228);
 --dg-slider-secondary: rgb(255, 255, 255);

 --dg-slider-enabled-primary: rgb(30, 30, 30);
 --dg-slider-enabled-secondary: rgb(255, 255, 255);

 /* For Enabled Categories */
 --dg-policy-option-heading-enabled-color: rgb(245, 80, 54);
}

:host(.dg-consent-banner) strong {
 font-weight: 500;
}

/* Slider styling for checked enabled categories */
:host(.dg-consent-banner)
 input[type="checkbox"]:not(:disabled):checked
 + label
 .dg-slider {
 background: rgb(30, 30, 30) !important;
}

/* Advanced button styling to match Groq Console */
:host(.dg-consent-banner) .dg-button {
 border-radius: 8px !important;
 padding: 8px 16px !important;
 font-weight: 500 !important;
 font-size: 14px !important;
 transition: all 0.2s ease !important;
}

:host(.dg-consent-banner) .dg-button.accept_all,
:host(.dg-consent-banner) .dg-button.accept_some {
 background: rgb(30, 30, 30) !important;
 color: rgb(248, 248, 247) !important;
 /* border: 1px solid rgb(245, 80, 54) !important; */
}

:host(.dg-consent-banner) .dg-button.accept_all:hover,
:host(.dg-consent-banner) .dg-button.accept_some:hover {
 background: oklch(44.6% 0.03 256.802) !important;
 /* border-color: rgb(220, 70, 48) !important; */
}

:host(.dg-consent-banner) .dg-button.reject_all,
:host(.dg-consent-banner) .dg-button.custom {
 background: rgb(243, 243, 242) !important;
 color: rgb(28, 25, 23) !important;
 border: 1px solid rgb(231, 229, 228) !important;
}

:host(.dg-consent-banner) .dg-button.reject_all:hover,
:host(.dg-consent-banner) .dg-button.custom:hover {
 background: rgb(231, 229, 228) !important;
}

/* Link styling to match Groq Console */
:host(.dg-consent-banner) .dg-link {
 color: rgb(245, 80, 54) !important;
 text-decoration: none !important;
 font-weight: 500 !important;
}

:host(.dg-consent-banner) .dg-link:hover {
 text-decoration: underline !important;
}

:host(.dg-consent-banner) .dg-main-content-policy-option-description p {
 margin-top: 0 !important;
 margin-bottom: 16px;
}

/* Dark mode support */
:host(.dg-consent-banner) .dark {
 --dg-consent-background-color: rgb(18, 20, 24);
 --dg-consent-background-border: rgba(153, 153, 153, 0.161);
 --dg-body-font-color: rgb(165, 160, 156);
 --dg-heading-font-color: rgb(248, 248, 247);
 --dg-title-font-color: rgb(248, 248, 247);
 --dg-policy-option-heading-color: rgb(248, 248, 247);
 --dg-button-secondary-background: rgb(38, 38, 38);
 --dg-button-secondary-color: rgb(248, 248, 247);
 --dg-slider-primary: rgba(153, 153, 153, 0.35);
 --dg-slider-background: rgb(107, 114, 128);
}

/* Overall banner container styling */
:host(.dg-consent-banner) .dg-app {
 box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px
 rgba(0, 0, 0, 0.1) !important;
 border: 1px solid rgb(231, 229, 228) !important;
}
